<?phpfunction visa_order_settings_init() {    register_setting( 'visa_order', 'visa_order_options' );    visa_order_csv_section();    visa_order_csv_details();}add_action( 'admin_init', 'visa_order_settings_init' );function visa_order_csv_section(){    add_settings_section(        'visa_order_design_styling',        __( 'Details', 'visa_order' ), 'visa_order_section_styling_data',        'visa_order'    );}function visa_order_section_styling_data( $args ) {    ?>    <p id="<?php echo esc_attr( $args['id'] ); ?>">        <?php esc_html_e( 'Please add form page url.', 'visa_order' ); ?>    </p>    <?php}function visa_order_options_page() {    add_menu_page(        'Visa Order Options',        'Visa Order Options',        'manage_options',        'visa_order',        'sb_visa_order_html'    );}add_action( 'admin_menu', 'visa_order_options_page' );function sb_visa_order_html() {    if ( ! current_user_can( 'manage_options' ) ) {        return;    }    ?>    <div class="wrap">        <h1><?php echo esc_html( get_admin_page_title() ); ?></h1>        <div class="tab-content">            <?php                max_design_tab();            ?>        </div>    </div>    <?php}function max_design_tab(){?>    <form action="options.php" method="post">        <?php        settings_fields( 'visa_order' );        do_settings_sections( 'visa_order' );        submit_button( 'Save Settings' );        ?>    </form><?php}